 TRUNCATE email_templates;  INSERT INTO email_templates VALUES('1' , '2' , 'NAME,VERIFY_LINK' , 'Registration Successful' , '<p>Hi {{NAME}},</p>\r\n\r\n<p>Thank you for registering your account on Skillbooker.com. Please verify your account, click on the link below.</p>\r\n\r\n<p><a href=\"https://www.skillbooker.com/users/verify/{{VERIFY_LINK}}\">Click Here To Verify</a><br />\r\nOR if you can click on the link copy and paste the following string into your URL<br />\r\n<br />\r\n<strong>https://www.skillbooker.com/users/verify/{{VERIFY_LINK}}</strong><br />\r\n&nbsp;</p>\r\n\r\n<p>Best Wishes,</p>\r\n\r\n<p>The Skillbooker Team</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2013-01-24' , '2019-01-06' ); INSERT INTO email_templates VALUES('3' , '2' , 'NAME,RESET_LINK' , 'Please reset your password' , '<p>Hello {{NAME}},</p>\r\n\r\n<p>You are receiving this email because a password reset has been requested for your account. Please click the link below to select your new password.</p>\r\n\r\n<p>{{RESET_LINK}}</p>\r\n\r\n<p>If you did not request to change your password, please ignore this email.</p>\r\n\r\n<p>Best Regards<br />\r\nSkillbooker Team</p>\r\n' , '' , '1' , '0' , '' , '2016-12-23' , '2018-10-01' ); INSERT INTO email_templates VALUES('2' , '2' , 'EMPLOYER_NAME,JOBSEEKER_NAME,JOB_TITLE,APPLICATION_LINK' , 'Job Application' , '<p>Hi {{EMPLOYER_NAME}},<br />\r\n<br />\r\n<strong>{{JOBSEEKER_NAME}}</strong> applied for <strong>{{JOB_TITLE}}</strong> on SKILLBOOKER.COM<br />\r\n<br />\r\nTo view the application go to {{APPLICATION_LINK}}<br />\r\n<br />\r\nBest Regards<br />\r\nTeam SkillBooker</p>\r\n' , '' , '1' , '0' , '' , '2018-10-09' , '2018-10-09' ); INSERT INTO email_templates VALUES('6' , '2' , 'FIRSTNAME,DESC' , 'Payment Received' , '<p>Hi {{FIRSTNAME}}</p>\r\n\r\n<p>You payment has been received at <strong>Skillbooker.com</strong>.</p>\r\n\r\n<p><strong>Description:</strong>{{DESC}}</p>\r\n\r\n<p><em>Best Regards<br />\r\nTeam SkillBooker</em></p>\r\n' , '' , '1' , '0' , '' , '2018-10-09' , '2018-10-12' ); INSERT INTO email_templates VALUES('4' , '2' , 'NAME,EMAIL,TEL,MESSAGE' , 'Notification of communication' , '<p>The following message has been send from Skillbooker.com</p>\r\n\r\n<p>NAME: {{NAME}}<br />\r\nEMAIL: {{EMAIL}}<br />\r\nTEL: {{TEL}}<br />\r\n<br />\r\n<strong>MESSAGE:</strong><br />\r\n{{MESSAGE}}</p>\r\n' , 'After using the contact page on the front end the person gets an email reassuring them that their communication has been received' , '1' , '0' , '' , '2018-06-15' , '2018-10-14' ); INSERT INTO email_templates VALUES('5' , '2' , 'SENDERNAME,RECEIVERNAME,TITLE,MESSAGE' , 'User communication on Skillbooker.com' , '<p>The following message has been send from Skillbooker.com</p>\r\n\r\n<p>SEND BY: <strong>{{SENDERNAME}}</strong> to <strong>{{RECEIVERNAME}}</strong></p>\r\n\r\n<p><strong>{{TITLE}}</strong></p>\r\n\r\n<p>{{MESSAGE}}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>To respond to this message please login and visit <a href=\"www.skillbooker.com/messenger\">Messenger</a></p>\r\n' , '' , '1' , '0' , '' , '2018-11-20' , '2018-11-20' ); INSERT INTO email_templates VALUES('7' , '2' , 'NAME,TITLELINK' , 'You have added a job to Skillbooker.com' , '<p>Hi {{NAME}}</p>\r\n\r\n<p><strong>{{TITLELINK}}</strong> has been added to our Job Board.&nbsp;</p>\r\n\r\n<p>Once you are logged in you can manage your jobs.</p>\r\n\r\n<p>Kind Regards</p>\r\n\r\n<p>Skillbooker Team</p>\r\n' , '' , '0' , '1' , 'gkolbe@gmail.com' , '2018-12-13' , '2018-12-19' ); INSERT INTO email_templates VALUES('8' , '2' , 'NAME,TITLELINK' , 'You have added a project to Skillbooker.com' , '<p>Hi {{NAME}}</p>\r\n\r\n<p><strong>{{TITLELINK}}</strong> has been added as a project&nbsp;</p>\r\n\r\n<p>Once you are logged in you can manage your projects.<br />\r\nPlease go to <a href=\"https://www.skillbooker.com/projects/list\">Project List</a></p>\r\n\r\n<p>Kind Regards</p>\r\n\r\n<p>Skillbooker Team</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-19' , '2018-12-19' ); INSERT INTO email_templates VALUES('9' , '2' , 'NAME,TITLELINK' , 'Congratulations you have won this project' , '<p>Congratulations {{NAME}} !<br />\r\n<br />\r\n{{TITLELINK}} has been allocated to you.<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nPlease start by reviewing the work and timescale go to <a href=\"https://www.skillbooker.com/bids\">Workflow</a>.<br />\r\nOnce work has been done the project manager will allocate payment to you.<br />\r\n&nbsp;</p>\r\n\r\n<p><em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-19' , '2018-12-19' ); INSERT INTO email_templates VALUES('10' , '2' , 'NAME,TITLELINK' , 'More hours has been added' , '<p>Hi {{NAME}} !<br />\r\n<br />\r\nIn {{TITLELINK}} more working hours has been allocated to you.<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nPlease start by reviewing the work and timescale go to <a href=\"https://www.skillbooker.com/bids\">Workflow</a>.<br />\r\nOnce work has been done the project manager will allocate payment to you.<br />\r\n&nbsp;</p>\r\n\r\n<p><em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-19' , '2018-12-19' ); INSERT INTO email_templates VALUES('11' , '2' , 'NAME,LINK' , 'Progress has been updated' , '<p>Hi {{NAME}} !<br />\r\n<br />\r\nProgress on project {{LINK}} has been updated.<br />\r\n<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nPlease start by reviewing the work and timescale go to <a href=\"https://www.skillbooker.com/bids\">Progress</a>.<br />\r\n&nbsp;</p>\r\n\r\n<p><em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-24' , '2018-12-24' ); INSERT INTO email_templates VALUES('12' , '2' , 'NAME,LINK' , 'More hours has been added to this project' , '<p>Hi {{NAME}} !<br />\r\n<br />\r\nMore hours has been created on project {{LINK}}.<br />\r\n<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nPlease start by reviewing the work and timescale go to <a href=\"https://www.skillbooker.com/bids\">Progress</a>.<br />\r\n&nbsp;</p>\r\n\r\n<p><em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-24' , '2018-12-24' ); INSERT INTO email_templates VALUES('13' , '2' , 'NAME,LINK' , 'Notes has been added to project' , '<p>Hi {{NAME}} !<br />\r\n<br />\r\nNotes to clarify things has been added on project {{LINK}}.<br />\r\n<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nPlease start by reviewing the work and timescale go to <a href=\"https://www.skillbooker.com/bids\">Workflow</a>.<br />\r\n&nbsp;</p>\r\n\r\n<p><em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-24' , '2018-12-24' ); INSERT INTO email_templates VALUES('14' , '2' , 'NAME,LINK,REASON' , 'A bid has been placed on your project' , '<p>Hi {{NAME}} !<br />\r\n<br />\r\n{{LINK}} has placed a bid on your project, please review his/her online CV and review his/her reasons.<br />\r\n{{REASON}}<br />\r\n<br />\r\nOnce you have logged in to Skillbooker.com.<br />\r\nGo to <a href=\"https://www.skillbooker.com/projects/list\">Projects</a>.<br />\r\n<br />\r\n<em>Please keep all communication via the Skillbooker messager system. If we need to settle issues between freelancer and project creators we will only use this communication evidence to arbitrate</em></p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , 'gkolbe@gmail.com' , '2018-12-24' , '2018-12-25' ); INSERT INTO email_templates VALUES('15' , '2' , 'KEYWORDS,SLUG' , 'Some comma separated words has been added' , '<p>Hi Gabriel<br />\r\n<br />\r\nSome comma seperated words has been added to a project<br />\r\n{{KEYWORDS}}<br />\r\n<br />\r\nAt&nbsp;<a href=\"https://www.skillbooker.com/projects/view/{{SLUG}}\">Project</a></p>\r\n\r\n<p>Kind Regards<br />\r\nTeam Skillbooker.com</p>\r\n' , '' , '1' , '1' , '' , '2019-01-07' , '2019-01-07' ); INSERT INTO email_templates VALUES('23' , '2' , 'NAME,TITLELINK' , 'A new Software has been added' , '<p>Hi {{NAME}}</p>\r\n\r\n<p><strong>{{TITLELINK}}</strong> has been added as a software &nbsp;</p>\r\n\r\n<p>Once you are logged in you can manage your softwares</p>\r\n\r\n<p>Kind Regards</p>\r\n\r\n<p>Skillbooker Team</p>\r\n' , '' , '0' , '0' , '' , '2019-02-01' , '2019-02-01' );